mapper.select:
SELECT E1.EMPLOYEE_ID, E1.EMPLOYEE_CODE, E1.NAME, E1.BORN_DATE, E1.EMAIL, E1.MOBIL, E1.JOIN_DATE, ( SELECT P1.NAME FROM HR_ORG_POSITION_B P1 WHERE P1.POSITION_ID = EA1.POSITION_ID ) POSITION_NAME, ( SELECT ( SELECT U1.NAME FROM HR_ORG_UNIT_B U1 WHERE U1.UNIT_ID = P1.UNIT_ID ) UNIT_NAME FROM HR_ORG_POSITION_B P1 WHERE P1.POSITION_ID = EA1.POSITION_ID ) UNIT_NAME FROM HR_EMPLOYEE E1 LEFT OUTER JOIN HR_EMPLOYEE_ASSIGN EA1 ON ( E1.EMPLOYEE_ID = EA1.EMPLOYEE_ID AND EA1.PRIMARY_POSITION_FLAG='Y')
E1.ENABLED_FLAG = #{enabledFlag}
AND EA1.POSITION_ID = #{positionId}
AND EA1.POSITION_ID IN (SELECT P1.POSITION_ID FROM HR_ORG_POSITION_B P1 WHERE P1.UNIT_ID = #{unitId} )
AND E1.EMPLOYEE_CODE LIKE CONCAT('%', CONCAT(#{employeeCode,jdbcType=VARCHAR}, '%'))
AND E1.NAME LIKE CONCAT('%', CONCAT(#{name,jdbcType=VARCHAR}, '%'))
AND E1.EMPLOYEE_ID NOT IN
#{item}
ORDER BY E1.EMPLOYEE_CODE ASC
