mapper.select:
SELECT PREFERENCES_ID, PREFERENCES, PREFERENCES_VALUE
  FROM SYS_PREFERENCES
 WHERE PREFERENCES =#{preferences,jdbcType=VARCHAR}
   AND USER_ID = #{userId,jdbcType=DECIMAL}

mapper.update:
UPDATE SYS_PREFERENCES
   SET PREFERENCES_VALUE = #{preferencesValue,jdbcType=VARCHAR},
       OBJECT_VERSION_NUMBER = OBJECT_VERSION_NUMBER + 1,
       LAST_UPDATE_DATE = CURRENT_TIMESTAMP

 PREFERENCES = #{preferences,jdbcType=VARCHAR}

  AND USER_ID = #{userId,jdbcType=DECIMAL}
